mapper.update:
update sys_menu
set status = #{status.value},
    update_time = #{updateTime},
    update_user = #{updateUser}
where menu_code = #{menuCode}

mapper.select:
select sm.menu_code menuCode, sm.menu_name menuName, sm.parent_code parentCode, psm.menu_name parentName, sm.button_code buttonCode, sm.menu_url menuUrl, sm.menu_icon menuIcon, sm.menu_sort menuSort, sm.menu_type menuType, sm.perms perms, sm.status status, sm.create_time createTime, csu.user_name createUserName, sm.update_time updateTime, usu.user_name updateUserName from sys_menu sm left join sys_menu psm on psm.menu_code = sm.parent_code left join sys_user csu on csu.user_id = sm.create_user left join sys_user usu on usu.user_id = sm.update_user
( sm.menu_code like CONCAT('%',#{item},'%') or sm.menu_name like CONCAT('%',#{item},'%') )
and sm.menu_type = #{menuType.value}
and sm.status = #{status.value}
