mapper.update:
update sys_role
set role_name = #{roleName,jdbcType=VARCHAR},
    role_type = #{roleType,jdbcType=CHAR},
    update_user = #{updateUser,jdbcType=INTEGER},
    update_time = #{updateTime,jdbcType=TIMESTAMP}
where role_id = #{roleId,jdbcType=INTEGER}

mapper.select:
select sr.role_id, sr.role_code, sr.role_name, sr.role_type, sr.role_status, csu.user_name, sr.create_time, usu.user_name, sr.update_time from sys_role sr left join sys_user csu on csu.user_id = sr.create_user left join sys_user usu on usu.user_id = sr.update_user
( su.role_code like CONCAT('%',#{item},'%') or su.role_name like CONCAT('%',#{item},'%') )
and su.role_type = #{roleType.value}
and su.role_status = #{roleStatus.value}
